import React, { useState } from 'react';
import { db } from '../';
import { collection, addDoc } from 'firebase/firestore';

function CriarPublicacao() {
  const [titulo, setTitulo] = useState('');
  const [descricao, setDescricao] = useState('');
  const [local, setLocal] = useState('');
  const [valor, setValor] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault(); // Evita o recarregamento da página
    try {
      // Adiciona um novo documento com os valores do formulário
      await addDoc(collection(db, "Publicacoes"), {
        Titulo: titulo,
        Descricao: descricao,
        Local: local,
        Valor: parseFloat(valor) // Garante que o valor é um número
      });
      alert('Publicação criada com sucesso!');
      // Limpa o formulário
      setTitulo('');
      setDescricao('');
      setLocal('');
      setValor('');
    } catch (error) {
      console.error("Erro ao adicionar publicação: ", error);
      alert('Erro ao criar publicação!');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Criar Publicação</h2>
      <label>
        Título:
        <input type="text" value={titulo} onChange={(e) => setTitulo(e.target.value)} required />
      </label>
      <label>
        Descrição:
        <textarea value={descricao} onChange={(e) => setDescricao(e.target.value)} required />
      </label>
      <label>
        Local:
        <input type="text" value={local} onChange={(e) => setLocal(e.target.value)} required />
      </label>
      <label>
        Valor (R$):
        <input type="number" value={valor} onChange={(e) => setValor(e.target.value)} required />
      </label>
      <button type="submit">Criar Publicação</button>
    </form>
  );
}

export default CriarPublicacao;
