import styled from 'styled-components';

export const StyledComponent = styled.div`
  @font-face {
    font-family: 'Oswald';
    src: url('/public/fonts/Oswald-Regular.ttf') format('ttf'),
         url('/public/fonts/Oswald-Bold.ttf') format('ttf');
  }
  font-family: 'Oswald', sans-serif;
`;

export const HeaderContainer = styled.div`
  background-color: #282c34;
  padding: 10px 20px;
  color: white;
  text-align: center;
`;


export const Nav = styled.nav`
  margin-top: 10px;
`;

export const NavLink = styled.a`
  color: white;
  margin-right: 15px;
  text-decoration: none;
  
  &:last-child {
    margin-right: 0;
  }

  &:hover {
    color: #61dafb;
  }
`;
